<template>
	<view class="login">
		<view class="content">
			<!-- 头部logo -->
			<view class="header">
				<image :src="logoImage"></image>
			</view>
			<!-- 主体表单 -->
			<view class="main">
				<wInput
					v-model="username"
					type="text"
					maxlength="11"
					placeholder="用户名"
					:focus="isFocus"
				></wInput>
				<wInput
					v-model="password"
					type="password"
					maxlength="11"
					placeholder="密码"
				></wInput>
			</view>
			<wButton 
				class="wbutton"
				text="登 录"
				:rotate="isRotate" 
				@click="startLogin"
			></wButton>
			
			
			
			<!-- 底部信息 -->
			<view class="footer">
				<navigator url="register" open-type="navigate">注册账号</navigator>
			</view>
		</view>
	</view>
</template>

<script>
	let _this;
	import wInput from '../../components/watch-login/watch-input.vue' //input
	import wButton from '../../components/watch-login/watch-button.vue' //button
	
	export default {
		data() {
			return {
				//logo图片 base64
				logoImage: 'data:image/png;base64,',
				username:'', //用户/电话
				password:'', //密码
				isRotate: false, //是否加载旋转
				isFocus: true // 是否聚焦
			};
		},
		components:{
			wInput,
			wButton,
		},
		mounted() {
			_this= this;
			//this.isLogin();
		},
		methods: {
			
			login() {
				this.$queue.showLoading('登录中...');
				this.$Request
					.login('/loginApp', {
						username: this.username,
						password: this.password
					})
					.then(res => {
						if (res.code == 200) {
							console.log(res)
							this.$queue.setData('token', res.token);
							this.$queue.setData("username", this.username);
							this.$queue.setData("password", this.password);
							// this.getUserInfo()
							uni.hideLoading();
							uni.switchTab({
								url: "../home/home"
							})
						} else {
							uni.hideLoading();
							this.$queue.showToast(res.msg);
							console.log(res.msg)
			
						}
					})
					.catch(res => {
						uni.hideLoading();
					});
			},
			startLogin() {
				if (this.username.length == 0) {
					this.$queue.showToast('请输入账号');
					return;
				}
				if (this.password.length == 0) {
					this.$queue.showToast('请输入密码');
					return;
				}
				this.login();
			},
			getUserInfo() {
				this.$Request.get('/getInfo').then(f => {
					console.log(f)
					if (f.code == 200) {
						console.log(f)
						let userInfo = f.userInfo
						userInfo.userId = f.user.userId
						userInfo.userName = f.user.userName
						console.log(userInfo)
						this.$queue.setData("UserInfo", userInfo)
					}
			
				})
			},
		}
	}
</script>

<style>
	@import url("../../components/watch-login/css/icon.css");
	@import url("./css/main.css");
</style>
